mapper.select:
SELECT week_accto_lrn_no
FROM week_accto_lrn
WHERE estbllctre_code = #{estbllctreCode}
  AND week = #{week}

mapper.delete:
DELETE FROM task
WHERE task_no = #{taskNo}
  AND week_accto_lrn_no IN (
    SELECT week_accto_lrn_no
    FROM week_accto_lrn
    WHERE estbllctre_code = #{estbllctreCode}
)

mapper.update:
UPDATE quiz_presentn
SET quiz_ex_code = NULL
WHERE quiz_code = #{quizCode}

UPDATE quiz
SET week_accto_lrn_no = #{weekAcctoLrnNo},
    ques_cn = #{quesCn},
    quiz_begin_de = #{quizBeginDe},
    quiz_clos_de = #{quizClosDe}
WHERE quiz_code = #{quizCode}

mapper.delete:
DELETE FROM quiz
WHERE quiz_code = #{quizCode}
  AND week_accto_lrn_no IN (
    SELECT week_accto_lrn_no
    FROM week_accto_lrn
    WHERE estbllctre_code = #{estbllctreCode}
)

mapper.select:
SELECT bbs_code
FROM bbs
WHERE estbllctre_code = #{estbllctreCode}
  AND bbs_nm = #{bbsNm}

SELECT ctt.bbsctt_no, ctt.acnt_id, ctt.bbs_code, ctt.parnts_bbsctt_no, ctt.file_group_no, ctt.bbsctt_sj, ctt.bbsctt_cn, ctt.bbsctt_writng_de, ctt.bbsctt_rdcnt, skl.sklstf_nm, std.stdnt_no, std.stdnt_nm FROM bbs_ctt ctt INNER JOIN bbs b ON ctt.bbs_code = b.bbs_code LEFT OUTER JOIN sklstf skl ON ctt.acnt_id = skl.acnt_id LEFT OUTER JOIN stdnt std ON ctt.acnt_id = std.acnt_id WHERE b.estbllctre_code = #{estbllctreCode}
AND ctt.bbs_code = #{bbsCode}
ORDER BY ctt.bbsctt_no DESC

mapper.update:
UPDATE bbs_ctt
SET bbs_code = #{bbsCode},
    bbsctt_sj = #{bbscttSj},
    bbsctt_cn = #{bbscttCn}
WHERE bbsctt_no = #{bbscttNo}

mapper.delete:
DELETE FROM bbs_ctt
WHERE bbsctt_no = #{bbscttNo}
  AND bbs_code IN (
    SELECT bbs_code FROM bbs WHERE estbllctre_code = #{estbllctreCode}
)

mapper.update:
UPDATE bbs_ctt
SET bbsctt_rdcnt = NVL(bbsctt_rdcnt,0) + 1
WHERE bbsctt_no = #{bbscttNo}

mapper.select:

SELECT *
FROM (
    SELECT bbsctt_no AS bbscttNo, bbsctt_sj AS bbscttSj
    FROM bbs_ctt
    WHERE bbs_code = #{code}
      AND bbsctt_no < #{no}
    ORDER BY bbsctt_no DESC
)
WHERE ROWNUM = 1


SELECT *
FROM (
    SELECT bbsctt_no AS bbscttNo, bbsctt_sj AS bbscttSj
    FROM bbs_ctt
    WHERE bbs_code = #{code}
      AND bbsctt_no > #{no}
    ORDER BY bbsctt_no ASC
)
WHERE ROWNUM = 1
