mapper.select:
select E.ESTBLLCTRE_CODE, E.ACQS_PNT, E.COMPL_SE, A.LCTRE_NM, E.ATNLC_NMPR, E.LCTRE_USE_LANG, E.EVL_MTHD, E.ESTBL_SEMSTR, E.LCTRE_CODE, E.PROFSR_NO, E.LCTRUM, A.LCTRE_CODE, A.OPER_AT, L.TIMETABLE_NO, L.ESTBLLCTRE_CODE, L.LCTRE_DFK, L.BEGIN_TM, L.END_TM ,S.SKLSTF_NM, ( select COUNT(R.ESTBLLCTRE_CODE) from ATNLC_REQST R where R.ESTBLLCTRE_CODE = E.ESTBLLCTRE_CODE and R.REQST_STTUS = '신청중') as TOTAL_REQST, ( select COUNT(R.ESTBLLCTRE_CODE) from ATNLC_REQST R where R.ESTBLLCTRE_CODE = E.ESTBLLCTRE_CODE and R.REQST_STTUS = '신청완료') as TOTAL_SUBMIT from ESTBL_COURSE E inner join ALL_COURSE A on (E.LCTRE_CODE = A.LCTRE_CODE) inner join LCTRE_TIMETABLE L on (E.ESTBLLCTRE_CODE = L.ESTBLLCTRE_CODE) inner join PROFSR P on (E.PROFSR_NO = P.PROFSR_NO) inner join SKLSTF S on (S.SKLSTF_ID = P.SKLSTF_ID) where A.OPER_AT != '2'
and a.lctre_nm like concat(concat('%',#{keyword}),'%')
and e.compl_se = #{complSe}

select E.ESTBLLCTRE_CODE, E.ACQS_PNT, E.COMPL_SE, A.LCTRE_NM, E.ATNLC_NMPR, E.LCTRE_USE_LANG, E.EVL_MTHD, E.ESTBL_SEMSTR, E.LCTRE_CODE, E.PROFSR_NO, E.LCTRUM, A.LCTRE_CODE, A.OPER_AT, L.TIMETABLE_NO, L.ESTBLLCTRE_CODE, L.LCTRE_DFK, L.BEGIN_TM, L.END_TM ,S.SKLSTF_NM from estbl_course E inner join all_course A on (E.lctre_code = A.lctre_code) inner join lctre_timetable L on (E.ESTBLLCTRE_CODE = L.ESTBLLCTRE_CODE) inner join profsr P on (E.profsr_no = P.profsr_no) inner join sklstf S on (S.sklstf_id = P.sklstf_id)
and E.profsr_no = #{profsrNo}
and (a.lctre_nm like '%' || #{keyword} || '%')
and e.compl_se = #{complSe}

mapper.update:
update ESTBL_COURSE
set	   LCTRUM = #{lctrum},
	   EVL_MTHD = #{evlMthd}
where  ESTBLLCTRE_CODE = #{estbllctreCode}

update LCTRE_TIMETABLE
set	   LCTRE_DFK = #{lctreDfk},
	   BEGIN_TM = #{beginTm},
	   END_TM = #{endTm}
where  ESTBLLCTRE_CODE = #{estbllctreCode}

update ESTBL_COURSE
set	   FILE_GROUP_NO = #{fileGroupNo}
where  ESTBLLCTRE_CODE = #{estbllctreCode}
