mapper.select:
SELECT COUNT(*)
FROM ESTBL_COURSE
WHERE PROFSR_NO = #{profsrNo}

SELECT EVL_NO 
FROM LCTRE_EVL
WHERE ESTBLLCTRE_CODE = #{estbllctreCode}

mapper.insert:
INSERT INTO LCTRE_EVL_SCORE
(LCTRE_EVL_SN, STDNT_NO, LCTRE_EVL_INNB, EVL_SCORE, EVL_CN)
VALUES (
    (SELECT NVL(MAX(TO_NUMBER(LCTRE_EVL_SN)), 0) + 1 FROM LCTRE_EVL_SCORE)
    ,#{stdntId}
    ,#{lctreEvlInnb}
    ,#{evlScore}
    ,#{evlCn}
)

INSERT INTO LCTRE_EVL (EVL_NO, ESTBLLCTRE_CODE, ALL_EVL_AVRG)
VALUES (
    #{evlNo}
    ,#{estbllctreCode}
    ,NULL
)

INSERT INTO LCTRE_EVL_IEM (LCTRE_EVL_INNB, EVL_NO, EVL_CN)
VALUES (
    (SELECT NVL(MAX(LCTRE_EVL_INNB), 0) + 1 FROM LCTRE_EVL_IEM)
    ,#{evlNo}
    ,#{evlCn}
)
